sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var cu = new ClassificationUtil();
    var items = cu.getAllClassifications().filter(function(a) { return a.portion_marking == pm.toUpperCase(); });
	if (items.length > 0)
		return items[0];
	var err = new sn_ws_err.ServiceError();
	err.setStatus(404);
	err.setMessage("Cant find Security Classification with portion marking:" + pm);
	err.setDetail("Valid portion marking of a Security Classification record is required to retrieve results. To get valid portion markings, use api/x_44813_sec_clsif/classificationhelper/all or api/x_44813_sec_clsif/classificationhelper/active endpoint to get valid Security Classification records.");
	throw err;
})(request, response);
